namespace AtTest.ForYellow
{
    class AGC017_D
    {
        static void Main(string[] args)
        {
            var sw = new System.IO.StreamWriter(OpenStandardOutput()) { AutoFlush = false };
            SetOut(sw);

            Method(args);

            Out.Flush();
        }

        static void Method(string[] args)
        {
            int n = ReadInt();
            List<int>[] graph = new List<int>[n];
            for(int i = 0; i < n; i++)
            {
                graph[i] = new List<int>();
            }
            for(int i = 0; i < n - 1; i++)
            {
                int[] xy = ReadInts();
                int x = xy[0] - 1;
                int y = xy[1] - 1;

                graph[x].Add(y);
                graph[y].Add(x);
            }

            List<List<int>> depthIndexes = new List<List<int>>();
            List<bool> state = new List<bool>();
            int[] parents = new int[n];
            int[] depthes = new int[n];
            var que = new Queue<int>();
            que.Enqueue(0);
            depthIndexes.Add(new List<int>());
            depthIndexes[0].Add(0);
            state.Add(true);
            parents[0] = -1;
            depthes[0] = 0;
            while (que.Count > 0)
            {
                int now = que.Dequeue();
                int nextDepth = depthes[now] + 1;
                for(int i = 0; i < graph[now].Count; i++)
                {
                    int to = graph[now][i];
                    if (to == parents[now])
                    {
                        continue;
                    }

                    que.Enqueue(to);
                    if (nextDepth>= depthIndexes.Count)
                    {
                        depthIndexes.Add(new List<int>());
                        state.Add(false);
                    }
                    depthIndexes[nextDepth].Add(to);
                    state[nextDepth] = !state[nextDepth];
                    parents[to] = now;
                    depthes[to] = nextDepth;
                }
            }

            int firstI = 1;
            while(firstI<state.Count&& !state[firstI])
            {
                firstI++;
            }

            if (firstI >= state.Count)
            {
                WriteLine("Bob");
                return;
            }

            int lastI = state.Count - 1;
            while (lastI >= 0 && !state[lastI])
            {
                lastI--;
            }

            for(int i = 0; i < depthIndexes[firstI].Count; i++)
            {
                var queTmp = new Queue<int>();
                queTmp.Enqueue(depthIndexes[firstI][i]);
                int tmpI = firstI;
                bool ok = true;
                while (queTmp.Count > 0)
                {
                    int cnt = queTmp.Count;
                    if (state[tmpI] != (cnt % 2 == 1))
                    {
                        ok = false;
                        break;
                    }

                    var queNext = new Queue<int>();
                    while (queTmp.Count > 0)
                    {
                        int now = queTmp.Dequeue();
                        for(int j = 0; j < graph[now].Count; j++)
                        {
                            int to = graph[now][j];
                            if (to == parents[now])
                            {
                                continue;
                            }

                            queNext.Enqueue(to);
                        }
                    }
                    queTmp = queNext;
                    tmpI++;
                }

                if (ok && tmpI > lastI)
                {
                    WriteLine("Alice");
                    return;
                }
            }

            WriteLine("Bob");
        }

        private static string Read() { return ReadLine(); }
        private static char[] ReadChars() { return Array.ConvertAll(Read().Split(), a => a[0]); }
        private static int ReadInt() { return int.Parse(Read()); }
        private static long ReadLong() { return long.Parse(Read()); }
        private static double ReadDouble() { return double.Parse(Read()); }
        private static int[] ReadInts() { return Array.ConvertAll(Read().Split(), int.Parse); }
        private static long[] ReadLongs() { return Array.ConvertAll(Read().Split(), long.Parse); }
        private static double[] ReadDoubles() { return Array.ConvertAll(Read().Split(), double.Parse); }
    }
}

