class Solver
{
    Scanner sc = new Scanner();
    //
    List<int>[] edge;
    public void Solve()
    {
        var N = sc.Int;

        edge = Create(N, () => new List<int>());
        for (var i = 0; i < N - 1; i++)
        {
            int a, b;
            sc.Make(out a, out b);
            a--; b--;
            edge[a].Add(b);
            edge[b].Add(a);
        }
        WriteLine(dfs(0, -1) == 1 ? "Bob" : "Alice");
    }
    int dfs(int idx, int p)
    {
        var c = 0;
        foreach (var e in edge[idx])
            if (e != p)
                c ^= dfs(e, idx);
        return c + 1;
    }
}

