var reader = bufio.NewReaderSize(os.Stdin, 1000000)
var writer = bufio.NewWriter(os.Stdout)
func NextLine() string {
  buffer := make([]byte, 0)
  for true {
    line, isPrefix, err := reader.ReadLine()
    if err != nil { panic(err) }
    buffer = append(buffer, line...)
    if !isPrefix { break }
  }
  return string(buffer)
}
func NextInt() int {
  n, _ := strconv.Atoi(NextLine())
  return n
}
func NextIntVec() []int {
  L := strings.Split(NextLine(), " ")
  N := make([]int, len(L))
  for i := range N {
    N[i], _ = strconv.Atoi(L[i])
  }
  return N
}
func Write(s interface{}) {
  fmt.Fprintln(writer, s)
}
func Output() {
  _ = writer.Flush()
}

func Grundy (T *[][]int, P *[]int, r int) int {
  if len((*T)[r]) < 2 { return 0 }
  G := 0
  for _, adj := range (*T)[r] {
    if adj == (*P)[r] { continue }
    G = G ^ (Grundy(T, P, adj) + 1)
  }
  return G
}

func Solve() {
  N := NextInt()
  T := make([][]int, N)
  for i := range T {
    T[i] = make([]int, 0)
  }
  for i := 1; i < N; i++ {
    XY := NextIntVec()
    x, y := XY[0] - 1, XY[1] - 1
    T[x] = append(T[x], y)
    T[y] = append(T[y], x)
  }
  P := make([]int, N)
  for i := range P {
    P[i] = -1
  }
  P[0] = N
  Q := make([]int, N)
  pos := 1
  for _, q := range Q {
    for _, adj := range T[q] {
      if 0 <= P[adj] { continue }
      P[adj] = q
      Q[pos] = adj
      pos++
    }
  }
  T[0] = append(T[0], N)
  ans := "Alice"
  if Grundy(&T, &P, 0) == 0 { ans = "Bob" }
  Write(ans)
  return
}

func main() {
  Solve()
  Output()
}

