		foreach (i; 0..n - 1)
		{
			int u, v;
			readf (" %s %s", &u, &v);
			u -= 1;
			v -= 1;
			a[u] ~= v;
			a[v] ~= u;
		}

		int recur (int v, int p)
		{
			int res = 0;
			foreach (u; a[v])
			{
				if (u != p)
				{
					res ^= recur (u, v) + 1;
				}
			}
			return res;
		}

