    dic[x].append(y)
    dic[y].append(x)
    child[x] += 1
    child[y] += 1

dep = [float("inf")] * N
dep[0] = 0
dq = deque([0])
while len(dq) > 0:
    n = dq.popleft()

    for i in dic[n]:
        if dep[i] > dep[n] + 1:
            dep[i] = dep[n] + 1


for i in range(N):

    if child[i] == 1:
        child[i] = 0
        gr[i] = 0
        heapq.heappush(q,[-1 * dep[i] , i])

while len(q) > 0:

    ngr = 0
    no = heapq.heappop(q)
    now = no[1]

    for i in dic[now]:
        if gr[i] != None:
            ngr ^= (gr[i] + 1)
        else:
            child[i] -= 1

            if child[i] <= 1 and gr[i] == None:
                heapq.heappush(q,[-1 * dep[i] , i])

