fn main() -> io::Result<()> {
    std::thread::Builder::new()
        .stack_size(64 * 1024 * 1024)
        .spawn(|| {
            let stdin = io::stdin();
            let stdout = io::stdout();
            run(KInput::new(stdin.lock()), io::BufWriter::new(stdout.lock()))
        })?
        .join()
        .unwrap();
    Ok(())
}

fn run<I: Input, O: Write>(mut kin: I, mut out: O) {
    macro_rules! output { ($($args:expr),+) => { write!(&mut out, $($args),+).unwrap(); }; }
    macro_rules! outputln {
        ($($args:expr),+) => { output!($($args),+); outputln!(); };
        () => { output!("\n"); if cfg!(debug_assertions) { out.flush().unwrap(); } }
    }

    let n: usize = kin.input();
    let mut g = vec![Vec::new(); n + 1];
    for (x, y) in kin.iter::<(usize, usize)>().take(n - 1) {
        g[x].push(y);
        g[y].push(x);
    }

    let gr = dfs(&g, 1, 0) - 1;
    if gr == 0 {
        outputln!("Bob");
    } else {
        outputln!("Alice");
    }
}

fn dfs(g: &[Vec<usize>], u: usize, p: usize) -> usize {
    let mut gr = 0;
    for &v in g[u].iter().filter(|&&v| v != p) {
        gr ^= dfs(g, v, u);
    }
    gr + 1
}

