#[derive(Debug)]
pub struct TreeRec {
    pub n: usize,
    pub dp: Vec<usize>,
}
impl TreeRec {
    pub fn new(n: usize) -> TreeRec {
        TreeRec {
            n: n,
            dp: vec![0; n],
        }
    }
    pub fn dfs(&mut self, u: usize, p: usize, graph: &Graph) {
        let mut res = 0;
        for v in graph.adjacency(u) {
            if v.to != p {
                self.dfs(v.to, u, graph);
                res ^= self.dp[v.to] + 1;
            }
        }
        self.dp[u] = res;
    }
