fn main() {
    let s = std::io::stdin();
    let mut sc = Scanner { stdin: s.lock() };

    // G(T')
    // G(0) = 0
    // G(1) = 1
    // G(S1) .. G(S N-1)
    // G(S1) + 1 = G(S1')
    // T => S1, S2, ...
    // {G(S1), G(S2), ...} = {0, 1, ..., G(T)-1}
    // T' => S1', S2', ..., 0
    // {G(S1') ,G(S2'), ..., 0 } = {G(S1)+1, G(S2)+1, ..., 0 } = {1, 2, ..., G(T), 0 }
    // G(T') = G(T) + 1

    let n: usize = sc.read();
    let mut graph = vec![vec![]; n];
    for _ in 1..n {
        let x = sc.read::<usize>() - 1;
        let y = sc.read::<usize>() - 1;
        graph[x].push(y);
        graph[y].push(x);
    }

    if grundy_dfs(0, 0, &graph) == 0 {
        println!("Bob");
    } else {
        println!("Alice");
    }
}

fn grundy_dfs(v: usize, p: usize, graph: &Vec<Vec<usize>>) -> usize {
    let mut grundy = 0;
    for &next in graph[v].iter() {
        if next == p {
            continue;
        }
        grundy ^= grundy_dfs(next, v, graph) + 1;
    }
    grundy
}

pub struct Scanner<R> {
    stdin: R,
}

impl<R: std::io::Read> Scanner<R> {
    pub fn read<T: std::str::FromStr>(&mut self) -> T {
        use std::io::Read;
        let buf = self
            .stdin
            .by_ref()
            .bytes()
            .map(|b| b.unwrap())
            .skip_while(|&b| b == b' ' || b == b'\n')
            .take_while(|&b| b != b' ' && b != b'\n')
            .collect::<Vec<_>>();
        unsafe { std::str::from_utf8_unchecked(&buf) }
            .parse()
            .ok()
            .expect("Parse error.")
    }
    pub fn vec<T: std::str::FromStr>(&mut self, n: usize) -> Vec<T> {
        (0..n).map(|_| self.read()).collect()
    }
    pub fn chars(&mut self) -> Vec<char> {
        self.read::<String>().chars().collect()
    }
}

use std::io::Read;

fn solve(g : &Vec<Vec<usize>>, pos : usize, prev : usize) -> bool {
    let mut child = 0;
    let mut win = 0;
    for &t in &g[pos] {
        if t == prev { continue }
        child += 1;
        if solve(g, t, pos) { win += 1 }
    }
    child%2 == 1 || win%2 == 1
}

fn main() {
    let mut buf = String::new();
    std::io::stdin().read_to_string(&mut buf).ok();
    let mut it = buf.split_whitespace();
    let n = it.next().unwrap().parse::<usize>().unwrap();
    let mut g = vec![Vec::new(); n];
    for _ in 0..n-1 {
        let x = it.next().unwrap().parse::<usize>().unwrap() - 1;
        let y = it.next().unwrap().parse::<usize>().unwrap() - 1;
        g[x].push(y);
        g[y].push(x);
    }
    println!("{}", if solve(&g, 0, n) { "Alice" } else { "Bob" });
}

use std::io;
use std::io::BufRead;

fn main() {
    let mut sc = Scanner::new(std::io::stdin());
    let n: usize = sc.next().unwrap();
    let mut graph: Vec<Vec<usize>> = vec![vec![]; n];
    for _ in 1..n {
        let mut x: usize = sc.next().unwrap();
        let mut y: usize = sc.next().unwrap();
        x -= 1;
        y -= 1;
        graph[x].push(y);
        graph[y].push(x);
    }
    let res = recur(&graph, 0, n);
    println!("{}", if res == 0 { "Bob" } else { "Alice" });
}

fn recur(graph: &Vec<Vec<usize>>, i: usize, p: usize) -> usize {
    let mut res = 0;
    for j in graph[i].iter() {
        if *j != p {
            res ^= recur(graph, *j, i) + 1;
        }
    }
    res
}

/// Scanner
///
/// # Example
/// ```
/// use contest::io::scanner;
/// let mut sc = scanner::Scanner::new("1 2 \n\n \r\t \n 3.5".as_bytes());
/// assert_eq!("1".to_string(), sc.next::<String>().unwrap());
/// assert_eq!(2, sc.next().unwrap());
/// assert_eq!(3.5, sc.next().unwrap());
/// assert_eq!(None, sc.next::<i32>());
/// ```

pub struct Scanner<R: io::Read> {
    br: io::BufReader<R>,
    // Read tokens are stored in reversed order per line.
    buf: Vec<String>,
}

impl<R: io::Read> Scanner<R> {
    pub fn new(r: R) -> Scanner<R> {
        Scanner {
            br: io::BufReader::new(r),
            buf: vec![],
        }
    }
    #[inline]
    pub fn next<T>(&mut self) -> Option<T>
        where T: std::str::FromStr,
              T::Err: std::fmt::Debug
    {
        self.next_string().map(|s| s.parse::<T>().expect("Parse failed: "))
    }
    fn next_string(&mut self) -> Option<String> {
        self.buf.pop().or_else(|| match self.update() {
            true => self.next_string(),
            false => None,
        })
    }
    #[inline]
    fn update(&mut self) -> bool {
        let mut s = String::new();
        let res = self.br.read_line(&mut s);
        match res.expect("I/O error.") {
            0 => false,
            _ => {
                self.buf = s.split_whitespace().map(|x| x.to_string()).rev().collect();
                true
            }
        }
    }
}

pub trait Zero: PartialEq + Sized {
    fn zero() -> Self;
    #[inline]
    fn is_zero(&self) -> bool {
        self == &Self::zero()
    }
}
pub trait One: PartialEq + Sized {
    fn one() -> Self;
