fn grundy_dfs(v: usize, p: usize, graph: &Vec<Vec<usize>>) -> usize {
    let mut xor = 0;
    for &next in graph[v].iter() {
        if p == next {
            continue;
        }
        xor ^= grundy_dfs(next, v, graph) + 1;
    }
    xor
}

