class PrimeList {

  private def sieve(xs: List[Int]): List[Int] =
    if (xs.isEmpty)
      Nil
    else
      xs.head :: sieve(xs.tail.filter(_ % xs.head != 0))

  private val primeList = sieve(Range(2, 1000000).toList)

  def isPrime(N: Int): Boolean = primeList.exists(N ==)

}

