namespace solution {
  class SolutionInterface {
  public:
    virtual ~SolutionInterface() {};
    virtual int run() = 0;

  protected:
    SolutionInterface() {}
    virtual void pre_calc() {}
    virtual bool action() = 0;
    virtual void init() {};
    virtual bool input( Storages& s ) { return false; };
    virtual void output( const Storages& s ) const {};
    virtual void before_action( const int& test_no ) const {}
    virtual void after_action( const int& test_no ) const {}
  };
}

// @snippet<sh19910711/contest:solution/solution-base.cpp>
namespace solution {
  class SolutionBase: public SolutionInterface {
  public:
    virtual ~SolutionBase() {}
    virtual int run() {
      this->pre_calc();
      for (;;) {
        this->before_action(-1);
        if ( ! this->action() ) { this->after_action(-1); break; }
        this->after_action(-1);
      };
      return 0;
    }
  protected:
    SolutionBase() {}
  };
}

namespace math {
  template <int SIZE> class PrimeChecker {
  public:
    void init() {
      std::fill( P + 0, P + SIZE, false );
      for ( int i = 3; i < SIZE; i += 2 )
        P[i] = true;
      P[2] = true;
      for ( int i = 3; i < SIZE; i += 2 ) {
        if ( P[i] )
          for ( int j = i + i; j < SIZE; j += i )
            P[j] = false;
      }
    }
    bool P[SIZE];
  };
}

