bool sosuuCheck(int n){
	if (n % 2 == 0)	return false;
	for (int i = 3;i<=sqrt((double)n);i+=2){
		if (n%i == 0)	return false;
	}
	return true;
}

int solve(int n){
	int ans = 0;
	for (int i = 3; i <= n / 2; i += 2){
		if (sosuuCheck(i)){
			if (sosuuCheck(n - i)){
				ans++;
			}
		}
	}
	return ans;
}

int main(){
	fastIO;
	int n;

	while (cin >> n, n){
		cout << solve(n) << endl;
	}
	return 0;
}

