    cin >> n;
    if (!n) break;
    ans = 0;
    for (int i=0; i<pv.size(); i++) {
      if (pv[i] >= n - pv[i]) break;
      if (p[n-pv[i]]) ++ans;
    }
    cout << ans << endl;
  }

  return 0;
}

