int memo[100000];
int dummy_prime(int n,int i){return memo[n] ? memo[n] : memo[n] = i>1 ? n%i ? dummy_prime(n,i-1) : -1 : 1;}
int isprime(int n){return memo[n] ? memo[n] : memo[n]=n>1 ? dummy_prime(n,sqrt(n)) : -1;}
int main()
{
	int n,result;

	while(cin >> n && n){
		result=0;
		for(int i=2;i<=n-i;i++)
			if(isprime(i)==1 && isprime(n-i)==1)result++;
		cout << result << endl;
	}
}

#include <iostream>
#include <string.h>

using namespace std;
bool IsPrime[1000001];

void calc()
{
	memset(IsPrime, 1, sizeof(IsPrime));
	IsPrime[0] = false;
	IsPrime[1] = false;
	for(int i = 2; i < 1000001; ++i)
	{
		if(IsPrime[i])
		{
			for(int j = 2 * i; j < 1000001; j += i)
			{
				IsPrime[j] = false;
			}
		}
	}
}

void solve()
{
	calc();
	int n;
	while(cin >> n, n)
	{
		int count = 0;
		int temp = n / 2;
		for(int i = 0; i <= temp; ++i)
		{
			if(IsPrime[i] == 1 && IsPrime[n - i] == 1)
			{
				count++;
			}
		}
		cout << count << endl;
	}
}

int main()
{
	solve();
	return(0);
}

