vector<ll> enum_prime(ll N){
	deque<bool> is_prime(N+1,true);
	vector<ll> P;
	for(ll i=2;i<=N;i++){
		if(is_prime[i]){
			for(ll j=i*i;j<=N;j+=i){
				is_prime[j] = false;
			}
			P.emplace_back(i);
		}
	}
	return P;
}

int main(){
	cin.tie(0);
	ios::sync_with_stdio(false);
	cout<<fixed<<setprecision(10);

	auto P=enum_prime(1000000);
	sort(ALL(P));
	int M=P.size();

	while(true){
		int N=in();
		if(!N) break;

		int sum = 0;
		for(int i=0;i<M;i++){
			int V=N-P[i];
			if(binary_search(P.begin()+i,P.end(),V)) sum++;
		}
		out(sum);
	}
	return 0;
}

