vector<int> get_prime(int n){
    if(n <= 1) return vector<int>();
    vector<bool> is_prime(n+1, true);
    vector<int> prime;
    is_prime[0] = is_prime[1] = 0;
    for (int i = 2; i <= n; ++i) {
        if(is_prime[i]) prime.emplace_back(i);
        for (auto &&j : prime){
            if(i*j > n) break;
            is_prime[i*j] = false;
            if(i % j == 0) break;
        }
    }
    return prime;
}

auto p = get_prime(1000000);

int main() {
    int n;
    while(cin >> n, n){
        int l = 0, ans = 0;
        for (int i = p.size()-1; i >= 0; --i) {
            while(l < i && p[l]+p[i] < n) l++;
            ans += (p[i]+p[l] == n);
        }
        cout << ans << "\n";
    }

    return 0;
}

