  is_prime[0]=is_prime[1]=false;
  for(int i=2;i<=1000000;i++){
    if(is_prime[i]){
      prime[p++]=i;
      for(int j=2*i;j<=1000000;j+=i) is_prime[j]=false;
    }
  }
  prime[p]=-1;
  int n;
  while(1){
    cin >> n;
    if(n==0) break;
    int ans=0;
    for(int i=0;i<1000000;i++){
      if(prime[i]==-1) break;
      if(prime[i]>n) break;
      int tmp=n-prime[i];
      if(tmp>=prime[i]){
	if(is_prime[n-prime[i]]) ans++;
      }
    }
    cout << ans << endl;
  }
  return 0;
}

