using namespace std;

static const int N = 1000000;
bool P[N+1];

void prime()
{
	int limit;

	for(int i=2; i<=N; i++)
	{
		P[i] = true;
	}

	limit = sqrt((double)N);
	for(int i=2; i<limit; i++)
	{
		if(P[i])
		{
			for(int j=(i<<1); j<=N; j+=i)
			{
				if(P[j])
				{
					P[j] = false;
				}
			}
		}
	}
}

int main(int argc, char** argv)
{
	int n, c;
	prime();

	while(cin >> n, n)
	{
		c = 0;
		if(P[n-2]) c++;

		for(int i=3; i<=n/2; i+=2)
		{
			if(P[i] && P[n-i])
			{
				c++;
			}
		}

		cout << c << endl;
	}

	return 0;
}

