bool sosuuCheck(int n){
	if (n % 2 == 0)	return false;
	for (int i = 3;i<=sqrt((double)n);i+=2){
		if (n%i == 0)	return false;
	}
	return true;
}

int solve(int n, set<int> &sosuuList){
	int ans = 0;
	foreach_it(set<int>, it, sosuuList){
		if ((*it) > n / 2)	break;
		if (sosuuList.find(n - (*it)) != sosuuList.end())
			ans++;
	}
