  const int half = *lower_bound(prime.begin(), prime.end(), n/2+1);
  int ans = 0;
  for (int i = 0; i < prime.size(); ++i) {
    if (half < prime[i]) break;
    const int value = n - prime[i];
    if (binary_search(prime.begin(), prime.end(), value)) ans++;
  }
  return ans;
}

