int prime[200];
int main(){
	int count=0;
	for(int i=3;i<1000;i+=2){
		bool t=true;
		for(int j=0;j<count;j++)
			if(i%prime[j]==0)t=false;
		if(t==true){
			prime[count]=i;
			count++;
		}
	}
	int n;
	while(true){
		cin>>n;
		if(n==0)
			break;
		n/=2;
		int ans=0,k=n*2-3;
		bool t;
		for(int i=3;i<=n;i+=2){
			t=true;
			for(int j=0;j<count;j++){
				if(i%prime[j]==0){
					if(i!=prime[j]){
						t=false;
						break;
					}
				}
				if(k%prime[j]==0){
					if(k!=prime[j]){
						t=false;
						break;
					}
				}
			}
			if(t)ans++;
			k-=2;
		}
		cout<<ans<<endl;
	}
	return 0;
}

