bool prime(int a) {
	if (a == 3)return true;
	for (int b = 3;; b+=2) {
		if (a%b == 0)return false;
		if ((b + 1)*(b + 1) > a)return true;
	}
}
int main() {
	while (1) {
		int s = 0;
		int a;
		cin >> a;
		if (a == 0)break;
		for (int b = 3; b < a / 2 + 1; b += 2) {
			if (prime(b) && prime(a - b))s++;
		}
		cout << s << endl;
	}
}

