  l=0;
  r=sosumi+1;
  while (l<r){
    m=(l+r)/2;
    if (sosu[m]>n){
      r=m;
    }else if (sosu[m]<n){
      l=m+1;
    }else{
      return 1;
    }
  }
  return 0;
}
