  cin.tie(0);
  ios_base::sync_with_stdio(false);
  cout << fixed << setprecision(10);

  sieve(1000000);
  int n;
  while (cin >> n, n) {
    int ans = 0;
    for (int i = 1; prime[i] <= n - prime[i]; i++) {
      ans += is_prime[n - prime[i]];
    }
    cout << ans << endl;
  }

  return 0;
}

