int makeHurui(void){

	int i, k;
	int m = 0;

	for(i=0; i<SIZE; i++){

		hurui[i] = 1;

	}

	hurui[0] = 0;
	hurui[1] = 0;

	for(i=2; i<SIZE; i++){

		if(hurui[i] == 1){

			for(k=i+i; k<SIZE; k+=i){

				hurui[k] = 0;

			}


			primeList[m] = i;
			m++;

		}

	}

	return m;

}



int check(int data[], int key, int pos, int to){

	int count = 0;

	if(data[pos] > key/2){

		return 0;

	}

	if(hurui[key - data[pos]] == 1){

		count++;
		//printf("%d, %d\n", data[pos], key-data[pos]);

	}

	return count + check(data, key, pos+1, to);

}

