import java.util.*;
public class Main {
	static boolean prime[] = new boolean[1000001];
	static List<Integer> hyou = new ArrayList<Integer>();
	static int sum[] = new int[1000001];
	static void doIt(){//sum[n]を埋める事を目指す
		for(int s=0;s<hyou.size();s++)for(int i=s;i<hyou.size();i++){
			if(hyou.get(i)+hyou.get(s)>=sum.length)continue;
			sum[hyou.get(s)+hyou.get(i)]++;
		}
	}
	public static void main(String[] args){
		Scanner in = new Scanner(System.in);
		for(int i=0;i<prime.length;i++)prime[i]=true;
		prime[0]=false;prime[1]=false;
		for(int s=0;s<prime.length;s++){
			if(prime[s]){
				for(int i=s+s;i<prime.length;i+=s)prime[i]=false;
				hyou.add(s);
			}
		}
		for(int i=0;i<sum.length;i++){
			if(prime[i])sum[i]=1;
			else sum[i]=0;
		}
		doIt();
		while(in.hasNext()){
			int n = in.nextInt();
			if(n==0)break;
			System.out.println(sum[n]);
		}
	}
}

import java.util.*;
public class Main 
{
	static boolean[] isPrime = new boolean[1000001];
	static List<Integer> prime = new ArrayList<Integer>();
	static void primeCalc()
	{
		for(int i = 0; i < isPrime.length; ++i)
		{
			isPrime[i] = true;
		}
		isPrime[0] = isPrime[1] = false;
		for(int i = 0; i < isPrime.length; ++i)
		{
			if(isPrime[i])
			{
				prime.add(i);
				for(int j = 2 * i; j < isPrime.length; j += i)
				{
					isPrime[j] = false;
				}
			}
		}
	}
	public static void main(String[] args) 
	{
		primeCalc();
		Scanner stdIn = new Scanner(System.in);
		while(stdIn.hasNext())
		{
			int n = stdIn.nextInt();
			if(n == 0)
			{
				break;
			}
			int count = 0;
			int pos = 0;
			while(prime.get(pos) <= n / 2)
			{
				if(isPrime[n - prime.get(pos)])
				{
					++count;
				}
				++pos;
			}
			System.out.println(count);
		}
	}
}

