def binary_search(num_list, num):
    left = 0
    right = len(num_list)

    while left <= right:
        mid = (left + right) // 2

        if num_list[mid] == num:
            return True
        elif num_list[mid] < num:
            left = mid + 1
        else:
            right = mid - 1

    return False


