import array
import bisect


def is_prime_number(num):
    limit = int(num ** 0.5)

    for lp in range(3, limit + 1, 2):
        if num % lp == 0:
            return False

    return True


def binary_search(num_list, num):
    left = 0
    right = len(num_list) - 1

    while left <= right:
        mid = (left + right) // 2

        if num_list[mid] == num:
            return True
        elif num_list[mid] < num:
            left = mid + 1
        else:
            right = mid - 1

    return False


