		if(i) E[P[i]].push_back(i);
	}

	if(N<=5000 && Q<=5000) {
		FOR(i,Q) {
			cin>>T[i]>>V[i]>>D[i];
			if(T[i]==1) {
				cin>>X[i];
				add(V[i],D[i],X[i]);
			}
			else if(T[i]==2) {
				cout<<add(V[i],D[i],0)<<endl;
			}
			else if(T[i]==3) {
				P[N]=V[i];
				A[N]=D[i];
				E[P[N]].push_back(N);
				N++;
			}
		}
	}
	else if(type2) {
		FOR(i,N) bt.add(i+1,i+1,A[i]);
		FOR(i,Q) {
			cin>>T[i]>>V[i]>>D[i];
			if(T[i]==1) {
				cin>>X[i];
				bt.add(V[i]+1,V[i]+D[i]+1,X[i]);
