template<class T>
struct BIT {
	struct Node {
		T sum0 = 0;
		T sum1 = 0;
	};

	vector<Node> nodes;

	BIT(int n) : nodes(n + 1) {}

	T sumRange(int k) {
		k++;
		T s0 = 0;
		T s1 = 0;
		int x = k;
		while (k > 0) {
			s0 += nodes[k].sum0;
			s1 += nodes[k].sum1;
			k -= k & -k;
		}
		return s0 + s1 * x;
	}

	T sumRange(int l, int r) {
		return sumRange(r - 1) - sumRange(l - 1);
	}

	T sumPoint(int k) {
		k++;
		T ret = 0;
		while (k > 0) {
			ret += nodes[k].sum1;
			k -= k & -k;
		}
		return ret;
	}

	void addPoint(int k, T v) {
		k++;
		while (k < nodes.size()) {
			nodes[k].sum0 += v;
			k += k & -k;
		}
	}

	void addRange(int l, int r, T value) {
		if (l == r) {
			return;
		}
		add(l, value);
		add(r, -value);
	}

	void add(int k, T v1) {
		T v0 = v1 * k;
		k++;
		while (k < nodes.size()) {
			nodes[k].sum0 -= v0;
			nodes[k].sum1 += v1;
			k += k & -k;
		}
	}
};

