#include <bits/stdc++.h>
using namespace std;

int N, Q, A[500005];
long long fw[500005], fw2[500005];
vector<int> children[500005];

long long dfs(int x, int l, int a) {
	A[x] += a;
	long long ans = A[x];
	if (l == 0) return ans;
	for (int i = 0; i < children[x].size(); i++) ans += dfs(children[x][i], l - 1, a);
	return ans;
}

void upd(int p, long long v) {
	p++;
	if (p == 0) return;
	for (int i = p; i <= 500000; i += (i & (-i))) fw[i] += v;
}

long long query(int p) {
	p++;
	long long ans = 0;
	for (int i = p; i > 0; i-= (i & (-i))) ans += fw[i];
	return ans;
}

void upd2(int p, long long v) {
	p++;
	if (p == 0) return;
	for (int i = p; i <= 500000; i += (i & (-i))) fw2[i] += v;
}

long long query2(int p) {
	p++;
	long long ans = 0;
	for (int i = p; i > 0; i-= (i & (-i))) ans += fw2[i];
	return ans;
}

void update(int a, int b, int v) {
	upd(a, v);
	upd(b + 1, -v);
	upd2(a, -(long long)(a - 1) * v);
	upd2(b + 1, (long long)(a - 1) * v);
	upd2(b + 1, (long long)(b - a + 1) * v);
}

long long sum(int a, int b) {
	a--;
	return query(b) * b + query2(b) - ((a == -1) ? 0 : (query(a) * a + query2(a)));
}

