static const int MAX_SIZE = 1 << 19; //segment tree 

typedef long long Int;
typedef long long ll;
Int all[2 * MAX_SIZE - 1], part[2 * MAX_SIZE - 1]; // segment tree

//[a, b)x.
void add(int a, int b, int x, int k = 0, int l = 0, int r = MAX_SIZE)
{
    if (a <= l && r <= b){ //[l, r)[a, b)
        all[k] += x; //[l, r)x.
    }
    else if (l < b && a < r){ //[l, r)[a, b)
        part[k] += (min(b, r) - max(a, l)) * x;  //, .
        add(a, b, x, k * 2 + 1, l, (l + r) / 2); //.
        add(a, b, x, k * 2 + 2, (l + r) / 2, r); //.
    }
}

Int sum(int a, int b, int k = 0, int l = 0, int r = MAX_SIZE)
{
    if (b <= l || r <= a){ //[a, b)[l, r)
        return (0);
    }
    else if (a <= l && r <= b){ //
        return (all[k] * (r - l) + part[k]);
    }
    else { //[l, r)[a, b)
        ll res;
        res = (min(b, r) - max(a, l)) * all[k]; //, [a, b).
        res += sum(a, b, k * 2 + 1, l, (l + r) / 2); //.
        res += sum(a, b, k * 2 + 2, (l + r) / 2, r); //
        return (res);
    }
}


