template<class V> class SegTree_3 {
public:
	int NV;
	vector<V> tot,each;
	void init(int count) {
		NV=1;
		while((1<<NV)<=count*2) NV++;
		tot.resize(NV*2,0);
		each.resize(NV*2,0);
	};

	ll getval(int x,int y,int l=0,int r=-1,int k=1) {
		if(r==-1) r=NV;
		if(r<=x || y<=l) return 0;
		if(x<=l && r<=y) return tot[k];
		return getval(x,y,l,(l+r)/2,k*2)+getval(x,y,(l+r)/2,r,k*2+1)+each[k]*(min(y,r)-max(x,l));
	}

	void update(int x,int y, V v,int l=0,int r=-1,int k=1) {
		if(r==-1) r=NV;
		if(l>=r) return;
		if(x<=l && r<=y) {
			each[k]+=v;
			tot[k]+=v*(r-l);
		}
		else if(l < y && x < r) {
			update(x,y,v,l,(l+r)/2,k*2);
			update(x,y,v,(l+r)/2,r,k*2+1);
			tot[k]=tot[2*k]+tot[2*k+1]+each[k]*(r-l);
		}
	}
};
