ll lazy[2000001];
ll sum[2000001];

void build(int id, int l, int r)
{
	if(r-l<2)
	{
		sum[id]=a[l];
		lazy[id]=0;
		return ;
	}
	int mid=(l+r)>>1;
	build(id*2,l,mid);
	build(id*2+1,mid,r);
	sum[id]=sum[id*2]+sum[id*2+1];
}

void push(int id, int l, int r)
{
	if(lazy[id]!=0)
	{
		sum[id]+=ll(r-l)*lazy[id];
		if(r-l>=2)
		{
			lazy[2*id]+=lazy[id];
			lazy[2*id+1]+=lazy[id];
		}
		lazy[id]=0;
	}
}

void update(int id, int l, int r, int ql, int qr, int v)
{
	push(id,l,r);
	if(l>=qr||ql>=r) return ;
	if(ql<=l&&r<=qr)
	{
		lazy[id]+=v;
		push(id,l,r);
		return ;
	}
	int mid=(l+r)>>1;
	update(id*2,l,mid,ql,qr,v);
	update(id*2+1,mid,r,ql,qr,v);
	sum[id]=sum[id*2]+sum[id*2+1];
}

ll query(int id, int l, int r, int ql, int qr)
{
	push(id,l,r);
	if(l>=qr||ql>=r) return 0;
	if(ql<=l&&r<=qr) return sum[id];
	int mid=(l+r)>>1;
	return (query(id*2,l,mid,ql,qr)+query(id*2+1,mid,r,ql,qr));
}

ll h[55001];
vi child[55001];
void dfs(ll u)
{
	for(ll i=0;i<adj[u].size();i++)
	{
		ll v =adj[u][i];
		h[v] = h[u] + 1;
		dfs(v);
	}
}

