	scanf("%d%d", &N, &Q);
	bool fail = 0;
	for (int i = 0; i < N; i++) {
		int p, s;
		scanf("%d%d", &p, &s);
		update(i, i, s);
		if (p != -1) children[p].push_back(i);
		A[i] = s;
		if (i != p + 1) fail = 1;
	}
	if (N <= 5000 && Q <= 5000) {
		while (Q--) {
			int q;
			scanf("%d", &q);
			if (q == 1) {
				int a, v, x;
				scanf("%d%d%d", &a, &v, &x);
				dfs(a, v, x);
			} else if (q == 2) {
				int a, v;
				scanf("%d%d", &a, &v);
				printf("%lld\n", dfs(a, v, 0));
			} else {
				int a, s;
				scanf("%d%d", &a, &s);
				children[a].push_back(N);
				A[N] = s;
				N++;
			}
		}
	} else if (!fail) {
