struct UnionFind
{
  vector< int > data;

  UnionFind(int sz)
  {
    data.assign(sz, -1);
  }

  void unite(int x, int y)
  {
    x = find(x), y = find(y);
    if(x != y) {
      if(data[x] > data[y]) swap(x, y);
      data[x] += data[y];
      data[y] = x;
    }
  }

  int find(int k)
  {
    if(data[k] < 0) return (k);
    return (data[k] = find(data[k]));
  }

  int size(int k)
  {
    return (-data[find(k)]);
  }
};

typedef long long int64;


const int backet = 1000;

int N, Q;
int64 C[800000];
vector< int > g[800000];
int T[400000], X[400000], Y[400000], Z[400000];
bool live[800000];

int in[800000], out[800000], ptr;


vector< int > upquery[800000];

void dfs(int idx)
{
  in[idx] = ptr++;
  for(int to : g[idx]) dfs(to);
  out[idx] = ptr;
}


int64 lazy[800001], add, sub;

void update(int idx, int depth = 0)
{
  for(auto &q : upquery[idx]) {
    add += Z[q];
    lazy[min(800000, depth + Y[q] + 1)] += Z[q];
  }
  sub += lazy[depth];
  C[idx] += add - sub;
  for(auto &to : g[idx]) {
    if(!live[to]) update(to, depth + 1);
  }
  sub -= lazy[depth];
  for(auto &q : upquery[idx]) {
    add -= Z[q];
    lazy[min(800000, depth + Y[q] + 1)] -= Z[q];
  }
  upquery[idx].clear();
}

int main()
{
  scanf("%d %d", &N, &Q);
  for(int i = 0; i < N; i++) {
    int a, b;
    scanf("%d %d", &a, &b);
    if(~a) g[a].push_back(i);
    C[i] = b;
  }

  for(int i = 0; i < Q; i++) {
    scanf("%d %d %d", &T[i], &X[i], &Y[i]);
    if(T[i] == 1) scanf("%d", &Z[i]);
  }

  int sz = N;
  for(int i = 0; i < Q; i++) {
    if(T[i] == 3) {
      live[sz] = true;
      g[X[i]].push_back(sz);
      C[sz] = Y[i];
      ++sz;
    }
  }

  dfs(0);

  sz = N;
  for(int i = 0; i < Q; i += backet) {
    int left = i, right = min(Q, i + backet);


    for(int j = left; j < right; j++) {
      if(T[j] == 1) upquery[X[j]].push_back(j);
    }
    update(0);
  }
}

