void add(int v,int d,int x){
	a[v] += x;
	if(d == 0) return;
	for(int i = 0;i < G[v].size();i++){
		add(G[v][i],d - 1,x);
	}
}

int sum(int v,int d){
	int s = a[v];
	if(d == 0) return s;
	for(int i = 0;i < G[v].size();i++){
		s += sum(G[v][i],d - 1);
	}
	return s;
}

int all[3000000],part[3000000];

void init(){
    n = 524288;
}

void add(int a,int b,int x,int k,int l,int r){
    if(b <= l || r <= a) return;
    if(a <= l && r <= b){
        all[k] += x;
        return;
    }
    part[k] += (min(b,r) - max(a,l)) * x;
    add(a,b,x,k * 2 + 1,l,(l + r) / 2);
    add(a,b,x,k * 2 + 2,(l + r) / 2,r);
}

int get(int a,int b,int k,int l,int r){
    int res = 0;
    if(a <= l && r <= b) return all[k] * (r - l) + part[k];
    if(b <= l || r <= a) return 0;
    res += all[k] * (min(b,r) - max(a,l));
    res += get(a,b,k * 2 + 1,l,(l + r) / 2);
    res += get(a,b,k * 2 + 2,(l + r) / 2,r);
    return res;
}

