vector<int> E[505050];

template<class V, int ME> class BIT_r {
public:
	V bit[2][1<<ME];
	BIT_r(){clear();};
	void clear() {ZERO(bit);};

	void update(int entry, V val0, V val1) {
		entry++;
		while(entry <= 1<<ME) bit[0][entry-1]+=val0, bit[1][entry-1] += val1, entry += entry & -entry;
	}
	V total(int entry) {
		int e=entry++;
		V v0=0,v1=0;
		while(entry>0) v0+=bit[0][entry-1], v1+=bit[1][entry-1], entry -= entry & -entry;
		return e*v0+v1;
	}
	void add(int L, int R, V val) { // add val to L<=x<=R
		update(L,val,-val*(L-1));
		update(R+1,-val,val*R);
	}
};
BIT_r<ll,23> bt;

ll add(int cur,int left,int v) {
	if(left<0) return 0;
	ll ret=0;
	A[cur]+=v;
	ret+=A[cur];
	FORR(e,E[cur]) ret+=add(e,left-1,v);
	return ret;
}

void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N>>Q;
	int type2=1;
	FOR(i,N) {
		cin>>P[i]>>A[i];
		if(P[i]+1!=i) type2=0;
		if(i) E[P[i]].push_back(i);
	}

	if(N<=5000 && Q<=0) {
		FOR(i,Q) {
			cin>>T[i]>>V[i]>>D[i];
			if(T[i]==1) {
				cin>>X[i];
				add(V[i],D[i],X[i]);
			}
			else if(T[i]==2) {
				cout<<add(V[i],D[i],0)<<endl;
			}
			else if(T[i]==3) {
				P[N]=V[i];
				A[N]=D[i];
				E[P[N]].push_back(N);
				N++;
			}
		}
	}
	else if(type2) {
		FOR(i,N) bt.add(i+1,i+1,A[i]);
		FOR(i,Q) {
			cin>>T[i]>>V[i]>>D[i];
			if(T[i]==1) {
				cin>>X[i];
				bt.add(V[i]+1,min(V[i]+D[i]+1,N),X[i]);
			}
			else if(T[i]==2) {
				cout<<bt.total(min(V[i]+D[i]+1,N))-bt.total(V[i])<<endl;
			}
			else if(T[i]==3) {
				P[N]=V[i];
				A[N]=D[i];
				bt.add(N+1,N+1,A[N]);
				N++;
			}
		}

	}
	else {
		assert(0);
	}

}


