#include <bits/stdc++.h>
using namespace std;

int N, Q, A[500005];
long long fw[900005], fw2[900005];
vector<int> children[500005];

long long dfs(int x, int l, int a) {
	A[x] += a;
	long long ans = A[x];
	if (l == 0) return ans;
	for (int i = 0; i < children[x].size(); i++) ans += dfs(children[x][i], l - 1, a);
	return ans;
}

void upd(int p, long long v) {
	p++;
	if (p == 0) return;
	for (int i = p; i <= 900000; i += (i & (-i))) fw[i] += v;
}

long long query(int p) {
	p++;
	long long ans = 0;
	for (int i = p; i > 0; i-= (i & (-i))) ans += fw[i];
	return ans;
}

void upd2(int p, long long v) {
	p++;
	if (p == 0) return;
	for (int i = p; i <= 900000; i += (i & (-i))) fw2[i] += v;
}

long long query2(int p) {
	p++;
	long long ans = 0;
	for (int i = p; i > 0; i-= (i & (-i))) ans += fw2[i];
	return ans;
}

void update(int a, int b, int v) {
	upd(a, v);
	upd(b + 1, -v);
	upd2(a, -(long long)(a - 1) * v);
	upd2(b + 1, (long long)(a - 1) * v);
	upd2(b + 1, (long long)(b - a + 1) * v);
}

long long sum(int a, int b) {
	a--;
	b = min(b, N - 1);
	return query(b) * b + query2(b) - ((a == -1) ? 0 : (query(a) * a + query2(a)));
}

int main() {
	scanf("%d%d", &N, &Q);
	bool fail = 0;
	for (int i = 0; i < N; i++) {
		int p, s;
		scanf("%d%d", &p, &s);
		update(i, i, s);
		if (p != -1) children[p].push_back(i);
		A[i] = s;
		if (i != p + 1) fail = 1;
	}
	if (N <= 5000 && Q <= 5000) {
		while (Q--) {
			int q;
			scanf("%d", &q);
			if (q == 1) {
				int a, v, x;
				scanf("%d%d%d", &a, &v, &x);
				dfs(a, v, x);
			} else if (q == 2) {
				int a, v;
				scanf("%d%d", &a, &v);
				printf("%lld\n", dfs(a, v, 0));
			} else {
				int a, s;
				scanf("%d%d", &a, &s);
				children[a].push_back(N);
				A[N] = s;
				N++;
			}
		}
	} else if (!fail) {
		while (Q--) {
			int q;
			scanf("%d", &q);
			if (q == 1) {
				int a, v, x;
				scanf("%d%d%d", &a, &v, &x);
				update(a, a + v, x);
			} else if (q == 2) {
				int a, v;
				scanf("%d%d", &a, &v);
				printf("%lld\n", sum(a, a + v));
			} else {
				int a, s;
				scanf("%d%d", &a, &s);
				assert(a == N - 1);
				update(N, N, s);
				N++;
			}
		}
	}

}

