template<class T, int SZ> struct BIT {
    T bit[SZ];
    void upd(int a, int b) { for (;a<SZ;a+=(a&-a)) bit[a] += b; }
    T sum(int a) { T res = 0; for (;a;a-=(a&-a)) res += bit[a]; return res; }
};

int N,Q,p[MX],a[MX],depth[MX];

int co;
vi child[MX], fake[MX], tmp;
vector<array<int,3>> inc, qnum[MX];
vpi qtree[MX], upd[MX];
BIT<ll,MX> B[3];

vl ans;

pi bound[MX];

int ord;
void genBound(int x) {
    bound[x].f = ord++;
    trav(t,child[x]) genBound(t);
    bound[x].s = ord-1;
}

void dfs(int x) {
    trav(y,qtree[x]) ans[y.s] -= B[0].sum(y.f);
    trav(y,qnum[x]) ans[y[2]] -= y[1]*B[1].sum(y[0]);
    trav(y,upd[x]) B[2].upd(y.f,y.s);

    B[0].upd(depth[x],a[x]); B[1].upd(depth[x],1);
    a[x] += B[2].sum(MX-1)-B[2].sum(depth[x]-1);
    trav(t,child[x]) dfs(t);

    trav(y,qtree[x]) ans[y.s] += B[0].sum(y.f);
    trav(y,qnum[x]) ans[y[2]] += y[1]*B[1].sum(y[0]);
    trav(y,upd[x]) B[2].upd(y.f,-y.s);
}

void calc() {
    inc.clear();
    F0R(i,2) B[i] = BIT<ll,MX>();
    dfs(0);
    F0R(i,co) {
        qnum[i].clear();
        qtree[i].clear();
        upd[i].clear();
    }
    // deal with inc ... 
    trav(t,tmp) {
        child[t].insert(child[t].end(),all(fake[t]));
        fake[t].clear();
    }
    FOR(t,N,co) {
        child[t].insert(child[t].end(),all(fake[t]));
        fake[t].clear();
    }
    tmp.clear();
    ord = 0; genBound(0);
    FOR(i,1,co) assert(bound[i] != mp(0,0));
    N = co; 
}

bool above(int a, int b) {
    return bound[a].f <= bound[b].f && bound[b].s <= bound[a].s;
}

void dfs0(int v, int d, int x) {
    if (depth[v] > d) return;
    if (v >= N) a[v] += x;
    trav(t,fake[v]) dfs0(t,d,x);
}

ll dfs1(int v, int d) {
    if (depth[v] > d) return 0;
    ll sum = (v >= N ? a[v] : 0);
    trav(t,fake[v]) sum += dfs1(t,d);
    return sum;
}

int main() {
    setIO(); re(N,Q);
    F0R(i,N) {
        re(p[i],a[i]);
        if (i) {
            tmp.pb(p[i]); fake[p[i]].pb(i);
            depth[i] = depth[p[i]]+1;
        } else depth[i] = 1;
    }
    co = N; N = 0; calc();
    // ps(N); F0R(i,N) ps(i,bound[i]); exit(0);
    for (int i = 0; i < Q; i += BLOCK) {
        FOR(j,i,min(i+BLOCK,Q)) {
            int t; re(t);
            if (t == 1) {
                int v,d,x; re(v,d,x); d += depth[v];
                if (v >= N) dfs0(v,d,x);
                else {
                    trav(t,tmp) if (above(v,t)) dfs0(t,d,x);
                    inc.pb({v,d,x}); upd[v].pb({d,x});
                }
            } else if (t == 2) {
                int v,d; re(v,d); d += depth[v];
                ans.pb(0);
                if (v >= N) {
                    ans.back() += dfs1(v,d);
                } else {
                    // ps("HA",t,v,d,tmp);
                    trav(t,tmp) if (above(v,t)) {
                        // ps("ZZ",sz(ans),t,d);
                        ans.back() += dfs1(t,d);
                    }
                    qtree[v].pb({d,sz(ans)-1});
                    trav(t,inc) {
                        if (above(t[0],v)) {
                            qnum[v].pb({min(t[1],d),t[2],sz(ans)-1});
                        } else if (above(v,t[0])) {
                            qnum[t[0]].pb({min(t[1],d),t[2],sz(ans)-1});
                        }
                    }
                }
            } else {
                re(p[co],a[co]); 
                depth[co] = depth[p[co]]+1;
                fake[p[co]].pb(co);
                if (p[co] < N && find(all(tmp),p[co]) == tmp.end()) {
                    tmp.pb(p[co]);
                }
                co ++;
            }
        }
        calc();
        // ps("CLR");
    }
