struct sum_seg{
	vector<long long int> lazy;
	vector<long long int> seg;
	int SS;
	sum_seg(){
	}
	void resize(int n){
		n *= 4;
		lazy.assign(n, 0);
		seg.assign(n, 0);
		SS = seg.size();
	}
	void update(int b,int rng){
		if (b * 2 + 2 < SS){
			lazy[b * 2 + 1] += lazy[b];
			lazy[b * 2 + 2] = lazy[b];
		}
		seg[b] += lazy[b]*rng;
		lazy[b] = 0;
	}
	inline void add(int b, int l, int r, int ll, int rr, int x){
		update(b,r-l);
		if (rr <= l || r <= ll){
			return;
		}
		if (ll <= l&&r <= rr){
			lazy[b] += x;
			update(b,r-l);
			return;
		}
		add(b * 2 + 1, l, (l + r) >> 1, ll, rr, x);
		add(b * 2 + 2, (l + r) >> 1, r, ll, rr, x);
		seg[b] = seg[b * 2 + 1]+ seg[b * 2 + 2];
	}
	inline int query(int b, int l, int r, int ll,int rr){
		update(b,r-l);
		if (r <= ll || ll < l)return -1;
		if (ll <= l&&r <= rr){
			return seg[b];
		}
		return query(b * 2 + 1, l, (l + r) >> 1, ll,rr) +query(b * 2 + 2, (l + r) >> 1, r, ll,rr);
	}
}; 

