#include <bits/stdc++.h>

using namespace std;

typedef long long int64;

struct SegmentTree
{
  vector< int64 > add, seg;
  int sz;

  SegmentTree(int n)
  {
    sz = 1;
    while(sz < n) sz <<= 1;
    seg.assign(2 * sz - 1, 0);
    add.assign(2 * sz - 1, 0);
  }

  void update(int a, int b, int x, int k, int l, int r)
  {
    if(a >= r || b <= l) return;
    if(a <= l && r <= b) {
      add[k] += 1LL * x;
      return;
    }
    update(a, b, x, 2 * k + 1, l, (l + r) >> 1);
    update(a, b, x, 2 * k + 2, (l + r) >> 1, r);
    seg[k] = seg[2 * k + 1] + seg[2 * k + 2];
    int mid = (l + r) >> 1;
    seg[k] += add[2 * k + 1] * (mid - l);
    seg[k] += add[2 * k + 2] * (r - mid);
  }

  void update(int a, int b, int x)
  {
    update(a, b, x, 0, 0, sz);
  }

  int64 query(int a, int b, int k, int l, int r)
  {
    if(a >= r || b <= l) return (0LL);
    if(a <= l && r <= b) return (seg[k] + add[k] * (r - l));
    return (query(a, b, 2 * k + 1, l, (l + r) >> 1) +
            query(a, b, 2 * k + 2, (l + r) >> 1, r) + add[k] * (min(b, r) - max(a, l)));
  }

  int64 query(int a, int b)
  {
    return (query(a, b, 0, 0, sz));
  }
};

struct SegmentTree2D
{
  vector< SegmentTree > add, seg;
  vector< vector< int > > vs;
  int sz;

  SegmentTree2D(int n)
  {
    sz = 1;
    while(sz < n) sz <<= 1;
    vs.resize(2 * sz - 1);
  }

  void set(int k, int height)
  {
    vs[k + sz - 1].push_back(height);
  }

  void build()
  {
    for(int k = sz - 1; k < vs.size(); k++) {
      sort(begin(vs[k]), end(vs[k]));
    }
    for(int k = sz - 2; k >= 0; k--) {
      vs[k].resize(vs[2 * k + 1].size() + vs[2 * k + 2].size());
      merge(begin(vs[2 * k + 1]), end(vs[2 * k + 1]),
            begin(vs[2 * k + 2]), end(vs[2 * k + 2]),
            begin(vs[k]));
    }
    for(int k = 0; k < vs.size(); k++) {
      add.emplace_back(SegmentTree(vs[k].size()));
      seg.emplace_back(SegmentTree(vs[k].size()));
    }
  }

  inline int lb(int k, int height)
  {
    return (lower_bound(begin(vs[k]), end(vs[k]), height) - begin(vs[k]));
  }

  int update(int a, int b, int Low, int High, int x, int k, int l, int r)
  {
    if(a >= r || b <= l) {
      return (0);
    } else if(a <= l && r <= b) {
      int latte = lb(k, Low), malta = lb(k, High);
      add[k].update(latte, malta, x);
      return (malta - latte);
    } else {
      int p = update(a, b, Low, High, x, 2 * k + 1, l, (l + r) >> 1) +
              update(a, b, Low, High, x, 2 * k + 2, (l + r) >> 1, r);
      int q = lb(k, Low);
      seg[k].update(q, q + p, x);
      return (p);
    }
  }

  void update(int a, int b, int Low, int High, int x)
  {
    update(a, b, Low, High, x, 0, 0, sz);
  }

  pair< int64, int > query(int a, int b, int low, int high, int k, int l, int r)
  {
    if(a >= r || b <= l) return {0, 0};
    if(a <= l && r <= b) {
      int latte = lb(k, low), malta = lb(k, high);
      auto vv = seg[k].query(latte, malta) + add[k].query(latte, malta);
      return {vv, malta - latte};
    }
    auto s = query(a, b, low, high, 2 * k + 1, l, (l + r) >> 1);
    auto t = query(a, b, low, high, 2 * k + 2, (l + r) >> 1, r);

    int latte = s.second + t.second;
    int malta = lb(k, low);
    auto q = add[k].query(malta, latte + malta);
    return {s.first + t.first + q, latte};
  }

  int64 query(int a, int b, int low, int high)
  {
    return (query(a, b, low, high, 0, 0, sz).first);
  }
};


int N, Q, cost[800000];
vector< int > g[800000];
int A[800000], B[800000], C[800000], D[800000];


int in[800000], out[800000], dep[800000], ptr;


void rec(int idx, int depth = 0)
{
  dep[idx] = depth;
  in[idx] = ptr++;
  for(int to : g[idx]) rec(to, depth + 1);
  out[idx] = ptr;
}


int main()
{
  scanf("%d %d", &N, &Q);
  for(int i = 0; i < N; i++) {
    int par;
    scanf("%d %d", &par, &cost[i]);
    if(~par) g[par].push_back(i);
  }

  int sz = N;
  for(int i = 0; i < Q; i++) {
    scanf("%d %d %d", &A[i], &B[i], &C[i]);
    if(A[i] == 1) scanf("%d", &D[i]);
  }

  rec(0);
  SegmentTree2D tree(sz);
  for(int i = 0; i < sz; i++) tree.set(in[i], dep[i]);
  tree.build();
  for(int i = 0; i < sz; i++) {
    tree.update(in[i], in[i] + 1, dep[i], dep[i] + 1, cost[i]);
  }

  for(int i = 0; i < Q; i++) {
    if(A[i] == 1) {
      tree.update(in[B[i]], out[B[i]], dep[B[i]], dep[B[i]] + C[i] + 1, D[i]);
    } else if(A[i] == 2) {
      printf("%lld\n", tree.query(in[B[i]], out[B[i]], dep[B[i]], dep[B[i]] + C[i] + 1));
    }
  }

}

