  for(int to : g[idx]) dfs(to);
  out[idx] = ptr;
}


int64 lazy[800001], add, sub;

void update(int idx, int depth = 0)
{
  for(auto &q : upquery[idx]) {
    add += Z[q];
    lazy[min(800000, depth + Y[q] + 1)] += Z[q];
  }
  sub += lazy[depth];
  C[idx] += add - sub;
  for(auto &to : g[idx]) {
    if(!live[to]) update(to, depth + 1);
  }
  sub -= lazy[depth];
  for(auto &q : upquery[idx]) {
    add -= Z[q];
    lazy[min(800000, depth + Y[q] + 1)] -= Z[q];
  }
  upquery[idx].clear();
}

