struct BIT{
	vector<long long int> bit;
	void resize(int N){
		bit.assign(N, 0);
	}
	void add(int i, long long int j){
		i++;
		while (i < bit.size()){
			bit[i] += j;
			i += i&-i;
		}
	}
	long long int sum(int i){
		long long int r = 0;
		i++;
		while (i){
			r += bit[i];
			i -= i&-i;
		}
		return r;
	}
};

