		cur_add += ev[b][i].second;
		flag[dep[b] + ev[b][i].first + 1] -= ev[b][i].second;
	}
	cost[b] += cur_add;
	for (int i = 0; i < v[b].size(); i++){
		dfs(v[b][i],cur_add);
	}
	for (int i = 0; i < ev[b].size(); i++){
		cur_add -= ev[b][i].second;
		flag[dep[b] + ev[b][i].first + 1] += ev[b][i].second;
	}
	ev[b].clear();
}

