inline void adding_all(int b){
	if (us[dep[b]] != u_s){
		us[dep[b]] = u_s;
		sum[dep[b]] = 0;
	}
	sum[dep[b]] += cost[b];
	for (int i = 0; i < v[b].size(); i++){
		adding_all(v[b][i]);
	}
	mx_dep = max(mx_dep, dep[b]);
}

inline void dfs3(int b, bool del=false){
	if (ex[b] == false)return;
	u_s++;
	mx_dep = 0;
	if (v[b].size() == 0){
		for (int i = 0; i < ask[b].size(); i++){
			ans[ask[b][i].id] += cost[b];
		}
		return;
	}
	int mx = -1;
	int node = 0;
	for (int i = 0; i < v[b].size(); i++){
		if (mx < child[v[b][i]]){
			mx = child[v[b][i]];
			node = v[b][i];
		}
	}
	for (int i = 0; i < v[b].size(); i++){
		if (v[b][i] != node){
			dfs3(v[b][i]);
		}
	}
	dfs3(node, true);
	for (int i = 0; i < v[b].size(); i++){
		if (v[b][i] != node){
			adding_all(v[b][i]);
		}
	}
	mx_dep = max(mx_dep, dep[b]);
	for (int i = dep[b]+1; i <= mx_dep; i++){
		sum[i] += sum[i - 1];
	}
	for (int i = 0; i < ask[b].size(); i++){
		int go = dep[b] + ask[b][i].d;
		go = min(go, mx_dep);
		ans[ask[b][i].id] = sum[go];
	}
}
