long long dfs(int x, int l, int a) {
	A[x] += a;
	long long ans = A[x];
	if (l == 0) return ans;
	for (int i = 0; i < children[x].size(); i++) ans += dfs(children[x][i], l - 1, a);
	return ans;
}

void upd(int p, long long v) {
	p++;
	if (p == 0) return;
	for (int i = p; i <= 1300000; i += (i & (-i))) fw[i] += v;
}

long long query(int p) {
	p++;
	long long ans = 0;
	for (int i = p; i > 0; i-= (i & (-i))) ans += fw[i];
	return ans;
}

void upd2(int p, long long v) {
	p++;
	if (p == 0) return;
	for (int i = p; i <= 1300000; i += (i & (-i))) fw2[i] += v;
}

long long query2(int p) {
	p++;
	long long ans = 0;
	for (int i = p; i > 0; i-= (i & (-i))) ans += fw2[i];
	return ans;
}

