class LazySegmentTree {
private:
	ll size_;
	vector<long long> v, lazy;
	void update(ll a, ll b, long long x, ll k, ll l, ll r) {
		push(k, l, r);
		if (r <= a || b <= l) return;
		if (a <= l && r <= b) {
			lazy[k] += x;
			push(k, l, r);
		}
		else {
			update(a, b, x, k * 2, l, (l + r) >> 1);
			update(a, b, x, k * 2 + 1, (l + r) >> 1, r);
			v[k] = merge(v[k * 2], v[k * 2 + 1]);
		}
	}
	long long query(ll a, ll b, ll k, ll l, ll r) {
		push(k, l, r);
		if (r <= a || b <= l) return 0;
		if (a <= l && r <= b) return v[k];
		long long lc = query(a, b, k * 2, l, (l + r) >> 1);
		long long rc = query(a, b, k * 2 + 1, (l + r) >> 1, r);
		return merge(lc, rc);
	}

public:
	LazySegmentTree() : v(vector<long long>()), lazy(vector<long long>()) {};
	LazySegmentTree(ll n) {
		for (size_ = 1; size_ < n;) size_ <<= 1;
		v.assign(size_ * 4, 0);
		lazy.assign(size_ * 4, 0);
	}
	inline void push(ll k, ll l, ll r) {
		if (lazy[k] != 0) {
			v[k] += lazy[k] * ll(r - l);
			if (r - l > 1) {
				lazy[k * 2] += lazy[k];
				lazy[k * 2 + 1] += lazy[k];
			}
			lazy[k] = 0;
		}
	}
	inline long long merge(long long x, long long y) {
		return x + y;
	}
	inline void update(ll l, ll r, long long x) {
		update(l, r, x, 1, 0, size_);
	}
	inline long long query(ll l, ll r) {
		return query(l, r, 1, 0, size_);
	}
};

