				printf("%lld\n", dfs(a, v, 0));
			} else {
				int a, s;
				scanf("%d%d", &a, &s);
				children[a].push_back(N);
				A[N] = s;
				N++;
			}
		}
	} else if (!fail) {
		while (Q--) {
			int q;
			scanf("%d", &q);
			if (q == 1) {
				int a, v, x;
