	cin>>N>>Q;
	int type2=1;
	FOR(i,N) {
		cin>>P[i]>>A[i];
		if(P[i]+1!=i) type2=0;
		if(i) E[P[i]].push_back(i);
	}

	if(N<=5000 && Q<=5000) {
		FOR(i,Q) {
			cin>>T[i]>>V[i]>>D[i];
			if(T[i]==1) {
				cin>>X[i];
				add(V[i],D[i],X[i]);
			}
			else if(T[i]==2) {
				cout<<add(V[i],D[i],0)<<endl;
			}
			else if(T[i]==3) {
				P[N]=V[i];
				A[N]=D[i];
				E[P[N]].push_back(N);
				N++;
			}
		}
	}
	else if(type2) {
		SegTree_3<ll> st;
		st.init(1<<20);
		FOR(i,N) st.update(i+1,i+2,A[i]);
		FOR(i,Q) {
			cin>>T[i]>>V[i]>>D[i];
			if(T[i]==1) {
				cin>>X[i];
				st.update(V[i]+1,V[i]+D[i]+1,X[i]);
			}
			else if(T[i]==2) {
				cout<<st.getval(V[i]+1,V[i]+D[i]+1)<<endl;
			}
			else if(T[i]==3) {
				P[N]=V[i];
				A[N]=D[i];
				st.update(N+1,N+2,A[N]);
				N++;
			}
		}

	}
	else {
		return;
	}

}


